package com.yy.android.lib.algorithm;

import java.util.HashMap;
import java.util.Map;

public class LeetCode76 {
    // 输入：s = "ADOBECODEBANC", t = "ABC"
    // 输出："BANC"

    public static void main(String[] args) {
        System.out.println(minWindow(
                "",
                ""));
        // System.out.println(minWindow("ABCDEFAADBC", "AABC"));
    }

    public static String minWindow(String s, String t) {
        if (s.length() < t.length()) {
            return "";
        }
        if (s.equals(t)) {
            return t;
        }
        int min = t.length();
        Map<Character, Integer> map = null;
        for (int i = 0; i <= s.length() - min; i++) {
            String s1 = s.substring(i, i + min);
            if (map == null) {
                map = new HashMap<>();
                for (Character character : s1.toCharArray()) {
                    if (map.containsKey(character)) {
                        map.put(character, map.get(character) + 1);
                    } else {
                        map.put(character, 1);
                    }
                }
            } else {
                Character c = s.charAt(i - 1);
                if (map.get(c) == 1) {
                    map.remove(c);
                } else {
                    map.put(c, map.get(c) - 1);
                }
                c = s.charAt(i + min - 1);
                if (map.containsKey(c)) {
                    map.put(c, map.get(c) + 1);
                } else {
                    map.put(c, 1);
                }
            }
            if (test(map, t)) {
                return s1;
            }
            if (i == s.length() - min) {
                if (min == s.length()) {
                    break;
                } else {
                    min++;
                    i = -1;
                    map = null;
                }
            }
        }
        return "";
    }

    private static boolean test(Map<Character, Integer> map, String t) {
        Map<Character,Integer> hashmap = new HashMap<>(map);
        for (Character character : t.toCharArray()) {
            if (!hashmap.containsKey(character)) {
                return false;
            } else {
                if (hashmap.get(character) <= 0) {
                    return false;
                }
                hashmap.put(character, hashmap.get(character) - 1);
            }
        }
        return true;
    }
}
